  const handleVolverAtras = () => {
    window.history.back();
  };

  return (
    <div className="container">
      <h1>Productos en Venta</h1>
      <button onClick={handleVolverAtras}>Volver Atrás</button>
      <br></br>
      <br></br>
      <div className="row">
        <div
          className="col-md-9"
          style={{ maxHeight: "600px", overflowY: "scroll" }}
        >
          <div className="row">
            {productos.map((producto) => (
              <div className="col-md-4 mb-4" key={producto.id}>
                <div className="card">
                  <div className="card-body">
                    <h5 className="card-title">{producto.nombre}</h5>
                    <p className="card-text">{producto.descripcion}</p>
                    <p className="card-text">Precio: ${producto.precio}</p>
                    {producto.stock > 0 ? (
                      <button
                        className="btn btn-primary"
                        onClick={() => agregarAlCarrito(producto)}
                      >
                        Agregar
                      </button>
                    ) : (
                      <p className="text-danger">Producto Agotado</p>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
        <div className="col-md-3">
          <div
            className="mt-2"
            style={{ maxHeight: "500px", overflowY: "scroll" }}
          >
            <h2>Ticket</h2>
            <table className="table table-sm">
              <thead>
                <tr>
                  <th>Producto</th>
                  <th>Cantidad</th>
                  <th>Precio</th>
                  <th>Acción</th>
                </tr>
              </thead>
              <tbody>
                {carrito.map((item, index) => (
                  <tr key={index}>
                    <td>{item.nombre}</td>
                    <td>
                      <input
                        type="number"
                        min="1"
                        className="form-control"
                        value={item.cantidad}
                        onChange={(e) =>
                          cambiarCantidad(item, parseInt(e.target.value))
                        }
                      />
                    </td>
                    <td>${(item.precio * item.cantidad).toFixed(2)}</td>
                    <td>
                      <button
                        className="btn btn-primary"
                        onClick={() => agregarAlCarrito(item)}
                      >
                        +
                      </button>
                      <button
                        className="btn btn-warning"
                        onClick={() => desagregarDelCarrito(item)}
                      >
                        -
                      </button>
                      <button
                        className="btn btn-danger"
                        onClick={() => eliminarDelCarrito(item)}
                      >
                        Quitar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <p className="total-venta">
            Total de Venta: <strong>${totalVenta.toFixed(2)}</strong>
          </p>
          {carrito.length > 0 && (
            <button className="btn btn-success" onClick={realizarPago}>
              Pago
            </button>
          )}
        </div>
      </div>
    </div>
  );
